		{
			"indicator": "Revenue",
			"description": "The total amount of money a company makes from selling goods or services.",
			"sources": ["Income Statement"],
			"importance": "High",
			"formula": "Total Sales - Returns",
			"formula_components": [
			{"component": "Total Sales", 
